// Ejecutar el comando para obtener la lista de impresoras
exec('wmic printer get name', $output);

// Verificar si se encontraron impresoras
if (empty($output)) {
    echo "No se encontraron impresoras locales.";
    exit;
}

// Crear el select con las opciones de impresoras
echo "<select name='impresora'>\n";
foreach ($output as $printer) {
    // Filtrar el resultado para eliminar líneas vacías y encabezados
    if (!empty(trim($printer)) && $printer != 'Name') {
        $nombre_impresora = trim($printer);
        echo "<option value='$nombre_impresora'>$nombre_impresora</option>\n";
    }
}
echo "</select>\n";
